yarn init && node -e "const fs = require('fs'); const deps = JSON.parse(fs.readFileSync('dependencies.json')); const pkg = JSON.parse(fs.readFileSync('package.json')); pkg.dependencies = {...pkg.dependencies, ...deps.dependencies}; pkg.devDependencies = {...pkg.devDependencies, ...deps.devDependencies}; fs.writeFileSync('package.json', JSON.stringify(pkg, null, 2));" && yarn install
